package data;

public class Test12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		double b = a; // int -> double (자동)
		
		double c = 1.5;
		int d = (int)c; // double -> int (강제)
		
		System.out.println(b);
		System.out.println(d);
		
		// 어떠한 경우에도 실수 > 정수
		
		
	}

}
