package condition3;

public class Test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	char key = 'w';
	
	if(key=='w') {
		System.out.println("앞으로 이동");
	}
	else if(key=='a') {
		System.out.println("왼쪽으로 이동"); 
	}
	else if(key=='s') {
		System.out.println("뒤로 이동");
	}
	else {
		System.out.println("오른쪽으로 이동");
	}
	
	// if문의 한 가지 단점 : 모든 상황마다 실행횟수가 다르다.
	
	
	
	
	
	}

}
