package loop2;

public class Test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// while
		
		for(int i =1; i<=10; i++) {
			System.out.println(i);
		}
		System.out.println();
		
		int n =1;
		while(n<=10) {
			System.out.println(n);
			n++;
		}
	}

}
